&#34;&gt;    root.left = aux(n, left, mid-&lt;span class=&#34;number&#34;&gt;1&lt;/span&gt;);	&lt;span class=&#34;comment&#34;&gt;// Compute the left (which is the mid in left side)&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    root.right = aux(n, mid+&lt;span class=&#34;number&#34;&gt;1&lt;/span&gt;, right);	&lt;span class=&#34;comment&#34;&gt;// Compute the right (which is the mid in right side)&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    &lt;span class=&#34;keyword&#34;&gt;return&lt;/span&gt; root;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&amp;#125;&lt;/span&gt;&lt;br&gt;&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;/figure&gt;



