ral&#34;&gt;null&lt;/span&gt;)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        &lt;span class=&#34;keyword&#34;&gt;return&lt;/span&gt; head;						&lt;span class=&#34;comment&#34;&gt;// No point in reversing empty or 1-sized list&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    &lt;span class=&#34;type&#34;&gt;ListNode&lt;/span&gt; &lt;span class=&#34;variable&#34;&gt;curr&lt;/span&gt; &lt;span class=&#34;operator&#34;&gt;=&lt;/span&gt; head, prev = &lt;span class=&#34;literal&#34;&gt;null&lt;/span&gt;;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    ListNode nextNode;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    &lt;span class=&#34;keyword&#34;&gt;while&lt;/span&gt; (curr != &lt;span class=&#34;literal&#34;&gt;null&lt;/span&gt;)&amp;#123;					&lt;span class=&#34;comment&#34;&gt;// While we haven&amp;#x27;t reached the tail&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        nextNode = curr.next;				&lt;span class=&#34;comment&#34;&gt;// Store the next node&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        curr.next = prev;					&lt;span class=&#34;comment&#34;&gt;// Current&amp;#x27;s next becomes it&amp;#x27;s previous&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        prev = curr;						&lt;span class=&#34;comment&#34;&gt;// Advance previous to current.&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        curr = nextNode;					&lt;span class=&#34;comment&#34;&gt;// Make current the actual next node&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    &amp;#125;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    &lt;span class=&#34;keyword&#34;&gt;return&lt;/span&gt; prev;							&lt;span class=&#34;comment&#34;&gt;// Current is at null, so it&amp;#x27;s previous is the&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&amp;#125;											&lt;span class=&#34;comment&#34;&gt;// new head.&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;/figure&gt;

