sum.length-&lt;span class=&#34;number&#34;&gt;1&lt;/span&gt;, idx1 = num1.length()-&lt;span class=&#34;number&#34;&gt;1&lt;/span&gt;, idx2 = num2.length()-&lt;span class=&#34;number&#34;&gt;1&lt;/span&gt;, carry = &lt;span class=&#34;number&#34;&gt;0&lt;/span&gt;, total = &lt;span class=&#34;number&#34;&gt;0&lt;/span&gt;;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    &lt;span class=&#34;type&#34;&gt;int&lt;/span&gt; n1, n2;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    &lt;span class=&#34;keyword&#34;&gt;while&lt;/span&gt; (idx1 &amp;gt;= &lt;span class=&#34;number&#34;&gt;0&lt;/span&gt; || idx2 &amp;gt;= &lt;span class=&#34;number&#34;&gt;0&lt;/span&gt;)&amp;#123;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        n1 = idx1 &amp;lt; &lt;span class=&#34;number&#34;&gt;0&lt;/span&gt; ? &lt;span class=&#34;number&#34;&gt;0&lt;/span&gt; : num1.charAt(idx1--)-&lt;span class=&#34;string&#34;&gt;&amp;#x27;0&amp;#x27;&lt;/span&gt;;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        n2 = idx2 &amp;lt; &lt;span class=&#34;number&#34;&gt;0&lt;/span&gt; ? &lt;span class=&#34;number&#34;&gt;0&lt;/span&gt; : num2.charAt(idx2--)-&lt;span class=&#34;string&#34;&gt;&amp;#x27;0&amp;#x27;&lt;/span&gt;;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        total = n1 + n2 + carry;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        carry = total/&lt;span class=&#34;number&#34;&gt;10&lt;/span&gt;;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        sum[index--] = (&lt;span class=&#34;type&#34;&gt;char&lt;/span&gt;)(total % &lt;span class=&#34;number&#34;&gt;10&lt;/span&gt; + &lt;span class=&#34;string&#34;&gt;&amp;#x27;0&amp;#x27;&lt;/span&gt;);&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    &amp;#125;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    &lt;span class=&#34;keyword&#34;&gt;if&lt;/span&gt; (carry == &lt;span class=&#34;number&#34;&gt;1&lt;/span&gt;)&amp;#123;&lt;/span&gt;&