&lt;span class=&#34;number&#34;&gt;0&lt;/span&gt;;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    &lt;span class=&#34;type&#34;&gt;int&lt;/span&gt; &lt;span class=&#34;variable&#34;&gt;right&lt;/span&gt; &lt;span class=&#34;operator&#34;&gt;=&lt;/span&gt; nums.length-&lt;span class=&#34;number&#34;&gt;1&lt;/span&gt;;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    &lt;span class=&#34;type&#34;&gt;int&lt;/span&gt; mid;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    &lt;span class=&#34;keyword&#34;&gt;while&lt;/span&gt; (nums[left] &amp;gt; nums[right]) &amp;#123;		&lt;span class=&#34;comment&#34;&gt;// While we are in the ascending order half,&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        mid = (left + right)/&lt;span class=&#34;number&#34;&gt;2&lt;/span&gt;;						&lt;span class=&#34;comment&#34;&gt;// Find the middle element&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        &lt;span class=&#34;keyword&#34;&gt;if&lt;/span&gt; (nums[mid] &amp;gt;= nums[left])			&lt;span class=&#34;comment&#34;&gt;// If mid element &amp;gt;= left element, then our min&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;            left = mid + &lt;span class=&#34;number&#34;&gt;1&lt;/span&gt;;								&lt;span class=&#34;comment&#34;&gt;// must be in the right half.&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        &lt;span class=&#34;keyword&#34;&gt;else&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;            right = mid;									&lt;span class=&#34;comment&#34;&gt;// otherwise min in the left half.&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    &amp;#125;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    &lt;span class=&#34;keyword&#34;&gt;return&lt;/span&gt; nums[left];										&lt;span class=&#34;comment&#34;&gt;// left points to minimum element.&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span clas